con:eventHandlers.con:script:
import groovy.json.JsonSlurper

def json = new JsonSlurper().parseText(testStepResult.getResponseContent())
def list;
if (!context.hasProperty("list")){
    list = [];
}
else {
    list = context.getProperty("list")
}
list.add('"unique_id":"'+json.unique_id+'"')
context.setProperty("list", list)
testRunner.testCase.setPropertyValue("responseList", list.toString())
